
jQuery(window).load(function(){
	initMap();
});

var isTouchDevice = /Windows Phone/.test(navigator.userAgent) || ('ontouchstart' in window) || window.DocumentTouch && document instanceof DocumentTouch;

function initMap(){
	jQuery('.maps-box').createMarker({
		holderMap: '.map-holder',
		wrapMap: '.map-wrap',
		activeClass: 'active',
		btnClose: 'a.popup-close',
		markerSrc: 'images/location-pin.png',
		template: 'inc/template_tooltip.mst',
		jsonData: 'map',
		activeClass: 'active',
		mapOptions: {
			zoom: 8,
			gestureHandling: 'cooperative',
			scrollwheel: true,
			panControl: true,
			zoomControl: true,
			scaleControl: true,
			mapTypeControl: false,
			streetViewControl: true,
			draggable: true
		}
	});
}

// scroll gallery init
function initCarousel() {
	jQuery('.carousel-blackquote').scrollGallery({
		mask: 'div.mask',
		slider: 'div.slideset',
		slides: 'div.slide',
		btnPrev: 'a.btn-prev',
		btnNext: 'a.btn-next',
		generatePagination: '.pagination',
		stretchSlideToMask: true,
		maskAutoSize: true,
		autoRotation: false,
		switchTime: 3000,
		animSpeed: 500,
		step: 1
	});
}

// load more init
function initLoadMore() {
	jQuery('.load-more-holder').loadMore({
		linkSelector: 'a.load-more',
		additionBottomOffset: 50,
		newContentTarget: '.post-holder'
	});
}

// initialize smooth anchor links
function initAnchors() {
	new SmoothScroll({
		anchorLinks: '.visual-section a[href^="#"]:not([href="#"])'
	});
}

// mobile menu init
function initMobileNav() {
	jQuery('body').mobileNav({
		menuActiveClass: 'nav-active',
		menuOpener: '.nav-opener'
	});
}

// handle flexible video size
function initFitVids() {
	jQuery('#main').fitVids();
}

// fancybox modal popup init
function initLightbox() {
	jQuery('a.lightbox').fancybox({
		helpers: {
			overlay: {
				css: {
					background: 'rgba(34, 33, 33, 0.6)'
				}
			}
		},
		afterLoad: function(current, previous) {
			// handle custom close button in inline modal
			if(current.href.indexOf('#') === 0) {
				jQuery(current.href).find('a.close').off('click.fb').on('click.fb', function(e){
					e.preventDefault();
					jQuery.fancybox.close();
				});
			}
		}
	});
}

